mapper.select:
SELECT funding_amount
FROM funding_joins
WHERE funding_join_id = #{fundingJoinId}

mapper.update:
UPDATE Funding_Conditions
SET 
    PERCENT = (COLLECTED_AMOUNT - #{amount}) / GOAL_AMOUNT * 100,
    COLLECTED_AMOUNT = COLLECTED_AMOUNT - #{amount}
WHERE 
    FUNDINGS_ID = #{fundingsId}

mapper.select:
SELECT percent
FROM Funding_Conditions
WHERE FUNDINGS_ID = #{fundingsId}
