mapper.select:

SELECT * 
FROM (
    SELECT p.*, ROW_NUMBER() OVER (ORDER BY p.created_at DESC) AS row_num
    FROM CONSULTATIONS p
    WHERE p.members_user_id = #{membersUserId}
)
WHERE row_num BETWEEN #{startRow} AND #{endRow}

SELECT COUNT(*)
FROM CONSULTATIONS
WHERE members_user_id = #{membersUserId}

mapper.update:
UPDATE CONSULTATIONS
SET title = #{title}, content = #{content}, reply = #{reply}, created_at = #{createdAt},
    is_reply = #{isReply}, members_user_id = #{membersUserId}, admin_user_id = #{adminUserId}
WHERE counsul_id = #{counsulId}
